select mother from Eg.Cat as mother, Eg.Cat as kit
where kit in elements(mother.Kittens)

select p from Eg.NameList list, Eg.Person p
where p.Name = some elements(list.Names)

from Eg.Cat cat where exists elements(cat.Kittens)

from Eg.Player p where 3 > all elements(p.Scores)

from Eg.Show show where 'fizard' in indices(show.Acts)

from Eg.Cat as fatcat where fatcat.Weight > ( select avg(cat.Weight) from Eg.DomesticCat cat )
from Eg.DomesticCat as cat where cat.Name = some ( select name.NickName from Eg.Name as name )
from Eg.Cat as cat where not exists ( from eg.Cat as mate where mate.Mate = cat )
from Eg.DomesticCat as cat where cat.Name not in ( select name.NickName from Eg.Name as name )

IQuery q = s.CreateQuery("from foo in class Foo where foo.Name=:Name and foo.Size=:Size");
q.SetProperties(fooBean); // fooBean has properties Name and Size
IList foos = q.List();

