				{
					root = new NumberedNode("root");
					await (s.PersistAsync(root));
					await (tx.CommitAsync());
				}
				ClearCounts();

				NumberedNode mergedChild;
				using (var tx = s.BeginTransaction())
				{
					var child = new NumberedNode("child");
