database-object.create:
CREATE OR REPLACE TRIGGER t_iu_part_gen_comp
BEFORE INSERT OR UPDATE ON part_gen_comp
FOR EACH ROW
BEGIN
    IF INSERTING THEN
        :new.gened := 1;
    ELSE
        :new.gened := :old.gened + 1;
    END IF;
END;

			CREATE OR ALTER TRIGGER t_iu_part_gen_comp 
			FOR part_gen_comp
            ACTIVE BEFORE INSERT OR UPDATE
			POSITION 0
			AS BEGIN
                IF (INSERTING) THEN
                    NEW.GENED = 1;
                ELSE
                    NEW.GENED = OLD.GENED + 1;
            END;
