database-object.create:
CREATE OR REPLACE TRIGGER t_iu_gen_prop
BEFORE INSERT OR UPDATE ON gen_prop
FOR EACH ROW
BEGIN
    IF INSERTING THEN
        :new.LastModified := HEXTORAW( '1' );
    ELSE
        :new.LastModified := HEXTORAW(
            TO_NUMBER( RAWTOHEX( :old.LastModified ) ) + 1
        );
    END IF;
END;
